// sorted by metacriticc score
export const getGames = () =>
  games.sort((gameA, gameB) => {
    if (gameA.metacritic > gameB.metacritic) return -1;
    if (gameA.metacritic < gameB.metacritic) return 1;
    return 0;
  });

export const getRandomGame = () => {
  const id = Math.floor(Math.random() * 60);
  return games[id];
};

export const getGenres = () => {
  const genres = games.reduce((current, next) => {
    next.newGenres.forEach((genre) => {
      if (!current.includes(genre)) current.push(genre);
    });
    return current;
  }, []);
  return genres;
};

export const getFilteredGames = (genre) =>
  games.filter((game) => game.newGenres.includes(genre));

export const getGameBySlug = (slug) => games.find((game) => game.slug === slug);

export default games;
